


CREATE TABLE NHACUNGCAP(
	MACC CHAR(15) NOT NULL,
	TENCC NVARCHAR(100),
	MAMATHANG CHAR(15),
	TENMATHANG NVARCHAR(100),
	LOAI NVARCHAR(20),
	SL INT,
	GIA MONEY,
	SDT_NHACUNGCAP VARCHAR(15),
	DIACHI NVARCHAR(100),

	constraint PK_NCC primary key(MACC,TENCC,SDT_NHACUNGCAP)
)

CREATE TABLE MANAGER(
	MAQL CHAR(15) NOT NULL,
	TENQL NVARCHAR(100),
	SDT_MANAGER VARCHAR(15),
	LUONG MONEY,
	THUONG FLOAT,
	GIOITINH CHAR(15),
	NGAYSINH DATE,
	DIACHI NVARCHAR(100),
	SOTHICH NVARCHAR(100),

	constraint PK_MANAGER primary key(MAQL,SDT_MANAGER)
) 

CREATE TABLE KHOHANG(
	MAKHO CHAR(15) NOT NULL,
	TENKHO NVARCHAR(100),
	TONGSL INT,
	BAONHIEULOAI INT,

	constraint PK_KHOHANG primary key(MAKHO,TENKHO)
)

CREATE TABLE MATHANG(
	MAMATHANG CHAR(15) NOT NULL,
	TENMATHANG NVARCHAR(100),
	LOAI NVARCHAR(30),
	SL INT,
	GIA MONEY,

	constraint PK_MATHANG primary key(MAMATHANG,TENMATHANG)
)

CREATE TABLE NHANVIEN(
	MANV CHAR(15) NOT NULL,
	TENNV NVARCHAR(100),
	LUONG MONEY,
	THUONG FLOAT,
	GIOITINH CHAR(15),
	NGAYSINH DATE,
	SDT_EMPLOYEE VARCHAR(15),
	SOTHICH NVARCHAR(100),
	DIACHI NVARCHAR(100),
	
	constraint PK_NHANVIEN primary key(MANV,SDT_EMPLOYEE)
)

CREATE TABLE KHACHHANG(
	MAKH CHAR(15) NOT NULL,
	TENKH NVARCHAR(100),
	SDT_KHACHHANG VARCHAR(15),
	DIACHI NVARCHAR(100),

	constraint PK_MAKH primary key(MAKH,SDT_KHACHHANG)
)


